import React, { useContext, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { makeStyles } from '@material-ui/core';
import Grid from '@material-ui/core/Grid';
import ItemCard from './ItemCard'
import {contextItem} from "../../../../contexts/ContextItem";


const ItemList = () => {
    const useStyles = makeStyles((theme) => ({
        container: {
            width: '100%',
            maxWidth: 1080,
            margin: '0 auto'
        }
    }))


    const { items, getItems, delItems, itemToEdit } = useContext(contextItem);
    const history = useHistory()
    const classes = useStyles()
    useEffect(() => {
        getItems()
    }, [])

    function handleClick(id) {
        itemToEdit(id)
        history.push('/edit')
    }

    return (
        <div>
            <h1 style={{ textAlign: 'center' }}>Products List</h1>

            <Grid container spacing={2} className={classes.container} >
                {
                    items.map(item => (
                        <Grid item xs={12} sm={6} lg={4} key={item.id}>
                            <ItemCard data={item}>
                            </ItemCard>
                            <button onClick ={()=>delItems(item.id)}>Delete</button>
                            <button onClick={()=>handleClick(item.id, history)}>Edit</button>
                        </Grid>
                    ))
                }
            </Grid>
        </div>
    )
}

export default ItemList;
